import React from "react";
import Game from "./components/game";
import "./App.css";

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <article>
          <h3>How to play</h3>
          <p>Write a 4-digit secret number.</p>
          <p>
            The digits must be all different. If the matching digits are in
            their right positions, they are "bulls", if in different positions,
            they are "cows".
          </p>
          <span>Example: </span>
          <ul>
            <li>Secret number: 4271</li>
            <li>Your try: 1234</li>
          </ul>
          <p></p>Answer: 1 bull and 2 cows. (The bull is "2", the
          cows are "4" and "1".) The first one to reveal the other's secret
          number in the least number of guesses wins the game.
        </article>
        <Game />
      </header>
    </div>
  );
}

export default App;
